import './App.css';
import { useEffect, useState } from 'react';
import { Octokit } from '@octokit/core';
import Markdown from 'marked-react';
// import Modal from 'react-modal'
import throttle from 'lodash/throttle';
// import dotenv from 'dotenv';
// dotenv.config();

const CLIENT_ID = 'Iv1.5f95480a214aafea';
// const { personalToken } = process.env;
const PersonalAccessToken  = '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';

const PER_PAGE = 10;

function App() {

  const [rerender, setRerender] = useState(false);
  const [userData, setUserData] = useState({});
  const [repositories, setRepositories] = useState([]);
  //issue
  const [issue, setIssue] = useState([]);
  const [loading, setLoading] = useState(false); 
  const [repoName, setRepoName] = useState('');
  const [owner, setOwner] = useState('');
  const [Title, setTitle] = useState('');
  const [Content, setContent] = useState('');
  const [IssueId, setIssueId] = useState();
  //add issue
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    const queryString = window.location.search;
    const urlParams = new URLSearchParams(queryString);
    const codeParam = urlParams.get("code");

    if (codeParam && localStorage.getItem("accessToken") === null) {
      async function getAccessToken() {
        await fetch('http://localhost:4000/getAccessToken?code=' + codeParam, {
          method: 'GET'
        }).then((response) => {
          return response.json();
        }).then((data) => {       
          if (data.access_token) {
            localStorage.setItem('accessToken', data.access_token);
            setRerender(!rerender);
          }
        });
      }
      getAccessToken();
    } else {
      getUserData();
    }
  }, []); //rerender


  useEffect(() => {
    if (localStorage.getItem('accessToken')) {
      getUserData();
    }
  }, [localStorage.getItem('accessToken')]);



  const getUserData = async () => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:4000/getUserData`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      });
      const data = await response.json();
      const octokit = new Octokit({ auth: accessToken });
      const repositoriesResponse = await octokit.request('GET /user/repos');
      setRepositories(repositoriesResponse.data);
      setUserData(data);
    } catch (error) {
      handleError(error);
    }
  };
  //得到儲藏庫
  // const getRepositories = async () => {
  //   try {
  //     const accessToken = localStorage.getItem('accessToken');
  //     if (!accessToken) return;
  //     const octokit = new Octokit({ auth: accessToken });
  //     const repositoriesResponse = await octokit.request('GET /user/repos');
  //     setRepositories(repositoriesResponse.data);
  //   } catch (error) {
  //     console.error('Error getting repositories:', error);
  //   }
  // };
  //得到文章
  const getIssues = async (repoName, owner) => {
    try {
        const accessToken = localStorage.getItem('accessToken');
        const octokit = new Octokit({ auth: accessToken });
        const issueResponse = await octokit.request(`GET /repos/${owner}/${repoName}/issues`, {
            per_page: PER_PAGE,
        });

        setIssue(prevIssue => [...prevIssue, ...issueResponse.data]);
        setLoading(false);
    } catch (error) {
      handleError(error);
    }
  };
  //滾輪偵測文章載入
  const handleScroll = throttle(async (e) => {
    const container = e.target;
    const bottom = container.scrollHeight - container.scrollTop === container.clientHeight;
    if (bottom && !loading && owner && repoName) {
      setLoading(true);

      const accessToken = localStorage.getItem('accessToken');
      const octokit = new Octokit({ auth: accessToken });

      try {
        const repoInfoResponse = await octokit.request(`GET /repos/${owner}/${repoName}`);
        const totalIssues = repoInfoResponse.data.open_issues_count;

        const currentIssuesCount = issue.length;

        if (currentIssuesCount >= totalIssues) {
          setLoading(false);
          return;
        }

        const nextPage = Math.ceil(currentIssuesCount / PER_PAGE) + 1;

        const issueResponse = await octokit.request(`GET /repos/${owner}/${repoName}/issues`, {
          per_page: PER_PAGE,
          page: nextPage
        });

        setIssue(prevIssue => [...prevIssue, ...issueResponse.data]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching additional issues:', error);
        setLoading(false);
      }
    }
  }, 500);

  const parseMarkdown = (markdownContent) => {
    return markdownContent; 
  };

  function logingithub() {
    window.location.assign(
      `https://github.com/login/oauth/authorize?client_id=${CLIENT_ID}&scope=repo%20read:user%20write:discussion%20repo:status%20write:repo`
    );
  }
  const handleError = (error) => {
    console.error('Error:', error);
  };
  // 編輯文章title&content
  const EditIssue = async (issueId, updatedBody) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      if (!accessToken) {
        console.error('Access token not found.');
        return;
      }
      const octokit = new Octokit({ auth: accessToken });
      if (!owner || !repoName) {
        console.error('Owner or repoName not found.');
        return;
      }
      const response = await octokit.request(`PATCH /repos/${owner}/${repoName}/issues/${issueId}`, {
        title: updatedBody.title,
        body: updatedBody.content
      });

      console.log(response);
      // 更新 issue 状态中对应 Issue 的内容
      setIssue(prevIssue => {
        return prevIssue.map(issueItem => {
          if (issueItem.id === issueId) {
            return { ...issueItem, title: updatedBody.title, body: updatedBody.content };
          }
          return issueItem;
        });
      });
      setIssueId(null);
    } catch (error) {
      handleError(error);
    }
  }
  // 删除文章
  const DeleteIssue = async (issueId) => {
    if (window.confirm('Are you sure you want to delete this issue?')) {
      try {
        const accessToken = localStorage.getItem('accessToken');
        const octokit = new Octokit({ auth: accessToken });

        // 使用 GitHub REST API 删除 Issue
        await octokit.request(`DELETE /repos/${owner}/${repoName}/issues/${issueId}`);

        // 从 issue 状态中移除被删除的 Issue
        setIssue(prevIssue => prevIssue.filter(issueItem => issueItem.id !== issueId));
      } catch (error) {
        handleError(error);
      }
    }
  }
  //編輯完成
  const EditingCompleted = async () => {
    const editedIssue = issue.find(item => item.id === editingIssueId);
    if (!editedIssue) return;

    if (editedIssue.title === editedTitle && editedIssue.body === editedContent) {
      setEditingIssueId(null);
      return;
    } 
    try {
      const accessToken = PersonalAccessToken;
      if (!accessToken) {
        console.error('Access token not found.');
        return;
      }
      const octokit = new Octokit({ auth: accessToken });
      if (!owner || !repoName) {
        console.error('Owner or repoName not found.');
        return;
      }
      console.log(editedIssue)
      console.log(repoName);
      console.log(IssueId.id);
      console.log(Title);
      console.log(Content);
      const response = await octokit.request(`PATCH /repos/${owner}/${repoName}/issues/${editedIssue.id}`, {
        title: editedTitle,
        body: editedContent
      });

      console.log(response);

      // 更新 issue 状态中对应 Issue 的内容
      setIssue(prevIssue => {
        return prevIssue.map(issueItem => {
          if (issueItem.id === editedIssue.id) {
            return { ...issueItem, title: editedTitle, body: editedContent };
          }
          return issueItem;
        });
      });
      setEditingIssueId(null);
    } catch (error) {
      handleError(error);
    }
  };  
  //  取消編輯
  const CancelEdit = async () => {
    setEditingIssueId(null);
  }

  // const closeModal = () => {
  //   setShowForm(false);
  // };

  const handleSubmit = async () => {
    try {
      const accessToken = PersonalAccessToken;
      console.log(accessToken);
      if (!accessToken) {
        console.error('Access token not found.');
        return;
      }
      const selectedRepo = repositories.find(repo => repo.name === repoName);
      if (!selectedRepo) {
        console.error(`Repository with name ${repoName} not found.`);
        return;
      }

      // console.log(accessToken);
      // console.log(owner);
      // console.log(repoName);
      const url = `https://api.github.com/repos/${owner}/${repoName}/issues`;

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${accessToken}`,
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          title: title,
          body: content
        }),
      });

      const responseData = await response.json();

      if (response.ok) {
        console.log('Issue created successfully:', responseData);
      } else {
        console.error(responseData);
      }
    } catch (error) {
      handleError(error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        {localStorage.getItem('accessToken') ?
          <>
            <h3>We have the access token</h3>
            <button onClick={() => { localStorage.removeItem('accessToken'); setRerender(!rerender); }}>Log out</button>

            <div>
              <h3>Your Repositories:</h3>
              <div className='repo'>
              {repositories.length > 0 && repositories.map((repo, index) => (
                <button
                  className='repo-list'
                  key={index}
                  onClick={() => {
                    setIssue([]); 
                    const selectedRepo = repositories[index]; // 获取所选仓库
                    setRepoName(selectedRepo.name); // 更新所选仓库的名称状态
                    setOwner(selectedRepo.owner.login); // 更新所选仓库的所有者状态
                    getIssues(selectedRepo.name, selectedRepo.owner.login); 
                    setShowForm(true);
                  }}
                >
                  {repo.name}
                </button>
              ))}
              </div>
            </div>
            <div className='c'>
              <h3>{repoName ? `${repoName} Issue:` : 'Issue'}</h3>
              {/* <button className='refresh c' onClick={getUserData}>refresh</button> */}
            </div>
            <div className='issue-list' onScroll={handleScroll}>
            {issue.length > 0 ? (
              issue.map((issueItem, index) => (
                <div className='issues' key={index}>
                  {editingIssueId === issueItem.id ? (
                    <div className='issue-column'>
                      <textarea 
                        className='issue-area'
                        value={editingIssueId === issueItem.id ? editedTitle : issueItem.title}
                        onChange={(e) => {
                          if (editingIssueId === issueItem.id) {
                            setEditedTitle(e.target.value);
                          } else {
                            setIssue(prevIssue => {
                              return prevIssue.map(item => {
                                if (item.id === issueItem.id) {
                                  return { ...item, title: e.target.value };
                                }
                                return item;
                              });
                            });
                          }
                        }}
                      />
                      <textarea 
                        className='issue-area'
                        value={editingIssueId === issueItem.id ? editedContent : issueItem.body}
                        onChange={(e) => {
                          if (editingIssueId === issueItem.id) {
                            setEditedContent(e.target.value);
                          } else {
                            setIssue(prevIssue => {
                              return prevIssue.map(item => {
                                if (item.id === issueItem.id) {
                                  return { ...item, body: e.target.value };
                                }
                                return item;
                              });
                            });
                          }
                        }}
                      />
                      <div>
                        <button className='btn' onClick={() => EditingCompleted(issueItem.id, editedTitle, editedContent)}>完成編輯</button>
                        <button className='btn' onClick={() => CancelEdit(issueItem.id)}>取消編輯</button>
                      </div>
                    </div>
                  ) : (
                    <div>
                      <Markdown>{parseMarkdown(`${index + 1}: ${issueItem.title}`)}</Markdown>
                      <Markdown>{parseMarkdown(`${issueItem.body}`)}</Markdown>
                      <button className='btn' onClick={(EditIssue) => {
                          setEditedTitle(issueItem.title);
                          setEditedContent(issueItem.body);
                          setEditingIssueId(issueItem.id);
                      }}>編輯</button>
                      <button className='btn' onClick={() => DeleteIssue(issueItem.id)}>刪除</button>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <p>No issues</p>
            )}
            {loading && <p>Loading...</p>}
          </div>
          <div>
          {showForm && (
            <div className='c cc'>
              <h3>新增 {repoName} Issue</h3>
              <textarea 
                placeholder='輸入標題' 
                value={title} 
                onChange={(e) => setTitle(e.target.value)}
              ></textarea>
              <textarea 
                placeholder='輸入內容' 
                value={content}
                onChange={(e) => setContent(e.target.value)}
              ></textarea>
              <div>
                <button type="button" onClick={handleSubmit}>新增</button>
                {/* <button type="button" onClick={closeModal}>關閉</button> */}
              </div>
            </div>
          )}
          </div>

          {/* <div><ReactModal repoName={repoName} addIssue={addIssue} showModal={showModal} setShowModal={setShowModal} issueId={repoName} /></div> */}
          </>
          :
          <>
            <h3>User is not logged in</h3>
            <div>
              <button onClick={logingithub}>Login With Github</button>
            </div>
          </>
        }

      </header>
    </div>
  );

}

export default App;
